import React from "react";



function Createserver(){

    let createRoom = async(e)=>{
       e.preventDefault();

       let data = {
        roomname: document.getElementById('room-name').value
       }

       let response = await fetch('/api/createserver', {
        method: 'POST',
        body: JSON.stringify(data),
        headers: {
            'Content-Type' : 'application/json'
        }
       });

       let json = await response.json();

       window.location.href = json.roomLink;

    }

    let serverFormStyle = {
        background: 'white',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center'

    }


    return(
        <>
            <div style={serverFormStyle} id = "server-form-container" className="container">
                <form action = "/api/createserver" method= "POST">
                    <input type = "text" id = "room-name" name = "room-name" placeholder="Room Name"/>
                    <input type = "submit" value = "Create Server" onClick={createRoom}/>
                </form>
            </div>
        </>
    );

}

export default Createserver;
