import React from "react";



function Createserver(){

    let createRoom = async(e)=>{
       e.preventDefault();

       let data = {
        roomname: document.getElementById('room-name').value
       }

       let response = await fetch('/api/createserver', {
        method: 'POST',
        body: JSON.stringify(data),
        headers: {
            'Content-Type' : 'application/json'
        }
       });

       let json = await response.json();

       window.location.href = json.roomLink;

    }

    let serverFormStyle = {
        background: 'white',
        marginLeft: '30%',
        marginRight: '30%',
        marginTop: '15%',
        borderRadius: '1rem',
        boxShadow: 'rgba(0, 0, 0, 0.35) 0px 5px 15px'
     

    }
    let formStyle = {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'spaceBetween'
    }

    let inputText = {
        width: '50%',
        height: '2em',
        marginBottom: '1em',
        border: '1px solid #294780',
        borderRadius: '0.3em',
        marginTop: '2rem'
    }

    let inputBtn={
        background: '#294780',
        fontSize: '1em',
        border: '0.5em solid #294780',
        borderRadius: '0.3em',
        marginTop: '1.5em',
        color: 'white',
        marginBottom: '2rem'
    }
    let groupIconFile = {
        position: 'absolute',
        zIndex: '-1',
        top: '10px',
        left: '8px',
        color: '#b8b8b8'
    }
    let groupIconLabel = {
        display: 'inline-block',
        padding: '5px 5px',
        cursor: 'pointer',
        borderRadius: '5px',
        backgroundColor: '#294780',
        color: '#fff'
    }


    return(
        <>
            <div style={serverFormStyle} id = "server-form-container" className="container">
                <form style = {formStyle} action = "/api/createserver" method= "POST">
                    <label style={groupIconLabel} for = "room-icon">Group Icon</label>
                    <input style={groupIconFile} type = "file" name = "room-icon" id = "room-icon" />
                    <input style={inputText} type = "text" id = "room-name" name = "room-name" placeholder="Room Name"/>
                    <input style={inputBtn} type = "submit" value = "Create Server" onClick={createRoom}/>
                </form>
            </div>
        </>
    );

}

export default Createserver;
