import React from "react";
import { withRouter } from "react-router-dom";
import {io} from "socket.io-client";
import { useEffect, useState } from "react";
let socket = io.connect("http://localhost:5000");

function Room(props){

    let [roomMembers, setRoomMembers] = useState([]);
  
    useEffect(()=>{
        let getRoomData = async()=>{
            let response = await fetch('/api/room/'+props.match.params.roomname);
            let json = await response.json();

            if(response.ok){
                //console.log(json.members);
                for(let i = 0; i < json.members.length; i++){
                    let ele = json.members[i];
                    //console.log(ele)
                    setRoomMembers([...roomMembers, ele]);
                }
                
                console.log(roomMembers);
            }
        }

        getRoomData();

      
    }, [])

    

    socket.on('connect', ()=>{
        console.log("Connected to the client")
    })
        
        
        

   

    return(
        <>
            <div>
                <h3>
                    Room {props.match.params.roomname}

                    
                </h3>
            </div>
        </>
    );

}

export default withRouter(Room);
