import React from "react";
import { useEffect } from "react";

function Home(){

    
    let theUser = null;

    useEffect(()=>{

        let json;
        async function fetchData(){ 
        let response = await fetch('/api/home')
        json = await response.json()
        //console.log(json)
        if(response.ok){
            
            if(json.toLink !== ''){
                window.location.href = json.toLink;
            }

         
            
        }

        theUser = json.thisUser
        console.log(theUser)
        document.getElementById('userInfo').innerHTML = theUser.username;

       
    }


    
    fetchData();

   

    }, [])

    let styleUser = {

        textAlign : 'center',
        margin: '0',




    }

    let userContainer = {

        background : 'white',
        margin: '0',
        padding: '0',
        height: '10vh'
    }

    return(
        <>
            <div className="container" id = "user-container" style={userContainer}>
               <h3 id = "userInfo" style={styleUser}></h3>
            </div>
        </>
    );

}

export default Home;
