import React from "react";
import { useEffect, useState } from "react";

function Yourprofile(){

    let [thisUser, setThisUser] = useState({})
    useEffect(()=>{
    
        let fetchData = async()=>{
            let response = await fetch('/api/profile');
            let json = await response.json();

            if(response.ok){
                console.log(json)
                setThisUser(json.user);
            }
        }

        fetchData();

    }, [])
    
    let profileDetailStyle = {
        background: 'white',
        margin: '0',
        padding: '0',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center'
    }

    return(
        <>
        <div style = {profileDetailStyle} id = "profile-container" className="container">
            <img src = {thisUser.img} style = {{paddingTop: "1.5em"}} width = "150" height = "150"/>
            <h2 style = {{color: "black" , marginTop: "1em"}}>{thisUser.username}</h2>
            <h3 style = {{color: "#9d9d9e", marginTop: "0"}}>{thisUser.firstname} {thisUser.lastname}</h3>
        </div>
        </>
    );


}


export default Yourprofile;
