import React from "react";


function Login(){

    let styleLoginFormContainer = {
        marginTop: '10em',
        marginLeft: '20em',
        marginRight: '20em',
        paddingBottom: '5em',
        textAlign: 'center',
        background: 'white',
        boxShadow: 'rgba(0, 0, 0, 0.35) 0px 5px 15px'
    }

    let inputTextPassword = {

        width: '30em',
        height: '2em',
        marginBottom: '1em',
        border: '1px solid #294780',
        borderRadius: '0.3em'

    }

    let loginBtn = {
         /*#335eb0*/
        background: '#294780',
        fontSize: '1em',
        border: '0.5em solid #294780',
        borderRadius: '0.3em',
        color: 'white'
    }

    let formsStyle = {
        
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'spaceBetween',
        alignItems: 'center',
    }

    let footerStyle = {
        background: '#363636',
        marginTop: '6em',
        textAlign: 'center',
        color: 'white'
    }

    return(
       <> 
        <div style = {styleLoginFormContainer} classname = "container" id = "login-form-container">
            <h1 style = {{ paddingTop: '2.5em', textAlign: 'center;'}}>Login</h1>
           <form style = {formsStyle} action = "#" method = "POST">
                <input style = {inputTextPassword} type = "text" id = "username" placeholder = "Username" />
                <input style = {inputTextPassword} type = "password" id = "password" placeholder = "Password" />
                <input style= {loginBtn} type = "submit" id = "login-btn" value= "Login" />
            </form>
            
        </div>

    <div style = {footerStyle} className="container" id = "footer">
        <h3>Developed by Kavish Tomar.</h3>
    </div>
    </>
        
    );


}

export default Login
