import React from "react";
import { useEffect, useState } from "react";


function Home(){

    
    let [theUser, setUser] = useState({});
    
   

    useEffect(()=>{

        let json;
        async function fetchData(){ 
        let response = await fetch('/api/home')
        json = await response.json()
        //console.log(json)
        if(response.ok){
            
            if(json.toLink !== ''){
                window.location.href = json.toLink;
            }
            setUser(json.thisUser);
         
            
        }
        
      
      
        

       
    }


    
    fetchData();

   

    }, [])

   

    let styleUser = {

        textAlign : 'center',
        margin: '0',
        padding: '0.6em'
        




    }

    let userContainer = {

        background : 'white',
        margin: '0',
        padding: '0',
        height: '8vh',
        boxShadow: 'rgba(0, 0, 0, 0.35) 0px 5px 15px',
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'center',
        alignItems: 'center'
    }

    return(
        <>
            <div className="container" id = "user-container" style={userContainer}>
                
                 <img src = {theUser.img} width = "50" height = "50" style={{borderRadius: '10px'}} /> 
                 <h3 id = "userInfo" style={styleUser}>Welcome {theUser.username} <a href = "/profile"><button style = {{background: "#335eb0", color:"white", border: "4px solid #335eb0", borderRadius: "5px", marginLeft: "1em"}} id="profile-btn">Profile</button></a> </h3>
                 <a href = "/createserver"><button style = {{background: "#335eb0", color:"white", border: "4px solid #335eb0", borderRadius: "5px", marginLeft: "1em"}} id="profile-btn">Create Room</button></a>
                 <a href = "/createserver"><button style = {{background: "#335eb0", color:"white", border: "4px solid #335eb0", borderRadius: "5px", marginLeft: "1em"}} id="profile-btn">Join Room</button></a>   
            </div>

            <div id = "friends-room-container" className = "container">
                <div id = "freinds-room-btn">
                    <button  style = {{background: "#335eb0", color:"white", border: "4px solid #335eb0", borderRadius: "5px", marginLeft: "1em"}}>Friends</button>
                    <button  style = {{background: "#335eb0", color:"white", border: "4px solid #335eb0", borderRadius: "5px", marginLeft: "1em"}}>Rooms</button>
                </div>
            </div>
        </>
    );

}

export default Home;
