import React from "react";
import { withRouter } from "react-router-dom";
import {io} from "socket.io-client";
import { useEffect, useState } from "react";
let socket = io.connect("http://localhost:5000");

function Room(props){

    let [roomMembers, setRoomMembers] = useState([]);

    let [currentUser, setCurrentUser] = useState("");

    let [otherMessages, setOtherMessages] = useState({});
  
    useEffect(()=>{

        
       
        let getRoomData = async()=>{

             
            let response = await fetch('/api/room/'+props.match.params.roomname);
            let json = await response.json();

            if(response.ok){
                    
                setRoomMembers(json.members);
                setCurrentUser(json.currentUser);

            }

            socket.on('userMsg', function(arg){
               

                setOtherMessages({
                    username: arg.username,
                    data: arg.data
                });

                document.getElementById('chat-container').innerHTML +="<div><h4>"+arg.username+"</h4><h6>"+arg.data+"</h6></div>"
            })
        }

        getRoomData();

        

       

      
    }, [])


    let membersChatContainer = {
        display: 'flex',
        flexDirection: 'column',
        marginLeft: '20%',
        marginRight: '20%',
        marginTop: '5%'

    }

    let memebersStyle = {
        background: 'white',
        display:'flex',
        flexDirection: 'row'
    }

    let chatAreaStyle = {
        background: 'white'
    }
    let chatcontainer = {
        height: '60vh'
    }

    let inputText = {
        width: '50%',
        height: '2em',
        marginBottom: '1em',
        border: '1px solid #294780',
        borderRadius: '0.3em'
    }

    let inputBtn = {
        background: '#294780',
        fontSize: '1em',
        border: '0.5em solid #294780',
        borderRadius: '0.3em',        
        color: 'white',
        marginLeft: '1em',
        marginBottom: '1.2em'
    }

    let chatInputDiv = {
        display : 'flex',
        flexDirection: 'row',
        justifyContent: 'center'
    }

    

    

    socket.on('connect', ()=>{
        console.log("Connected to the client")
    })

    let params = {
        room: props.match.params.roomname
    }

    socket.emit('join', params, function(err){
        console.log(err);
    } )

   
    

    let sendMsg = (e)=>{
        e.preventDefault();
       
        let message = document.getElementById('msg-writer').value;
        
        socket.emit("roomMsg",{
            username : currentUser,
            message: message
        });

        document.getElementById('chat-container').innerHTML += "<div><h4>You</h4><h6>"+message+"</h6></div>"


    }
        
        
    

   

    return(
        <>
            <div style = {membersChatContainer} className="container" id = "memebers-chat-container">
                  
                  <div style = {memebersStyle} id = "members-drop-down-container">
                     {roomMembers.map((users)=>{
                        return <h4 style = {{marginLeft: '2em', marginRight: '2m',  borderRight: '2px solid blue', height: '100', width: '12vw'}}>{users}</h4>
                      })}
                  </div>
                    <br/>
                  <div style = {chatAreaStyle} id = "chat-area">
                       <div style = {chatcontainer} id = "chat-container">

                       </div>
                       <div style =  {chatInputDiv} id = "chat-inputs">
                            <input style = {inputText} type = "text" id = "msg-writer" placeholder="Write text..."/>
                            <button style = {inputBtn} id = "send-btn" onClick={sendMsg}>Send</button>
                       </div>
                  </div>
                    
            </div>
        </>
    );

}

export default withRouter(Room);
