import React from "react";
import { useEffect, useState } from "react";
import { Link } from "react-router-dom";



function Home(){

    
     let [theUser, setUser] = useState({});

     let [rooms, setRooms] = useState([]);
    
        

    useEffect(()=>{

        let json;
        async function fetchData(){ 
        let response = await fetch('/api/home')
        json = await response.json()
        //console.log(json)
        if(response.ok){
            
            if(json.toLink !== ''){
                window.location.href = json.toLink;
            }
            console.log(json.thisUser);
            setUser(json.thisUser);

            console.log(json.allServers);

            setRooms(json.allServers)
           
           
        }
        
      
      
        

       
    }


    
    fetchData();

   

    }, [])

  

    let styleUser = {

        textAlign : 'center',
        margin: '0',
        padding: '0.6em'
        




    }

    let userContainer = {

        background : 'white',
        marginTop: '0',
        padding: '0',
        height: '8vh',
        boxShadow: 'rgba(0, 0, 0, 0.35) 0px 5px 15px',
        display: 'flex',
        flexDirection: 'row',
        justifyContent: 'center',
        alignItems: 'center'
    }

    let friendsRoomContainer = {
        background: 'white',
        marginTop: '5%',
        marginLeft: '20%',
        marginRight: '20%',
        height: '50vh',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        
    }

    let [buttonColor , setHoverColor] = useState("white");

    let [buttonTextColor, setButtonTextColor] = useState("black")

    return(
        <>
            <div className="container" id = "user-container" style={userContainer}>
                
                 <img src = {theUser.img} width = "50" height = "50" style={{borderRadius: '10px'}} /> 
                 <h3 id = "userInfo" style={styleUser}>Welcome {theUser.username} <Link to = "/profile"><button style = {{background: "#335eb0", color:"white", border: "4px solid #335eb0", borderRadius: "5px", marginLeft: "1em"}} id="profile-btn">Profile</button></Link> </h3>
                 <Link to = "/createserver" ><button style = {{background: "#335eb0", color: "white", border: "4px solid #335eb0", borderRadius: "5px", marginLeft: "1em"}} id="profile-btn">Create Room</button></Link>
                 <Link to = "/joinserver" ><button style = {{background: "#335eb0", color: "white", border: "4px solid #335eb0", borderRadius: "5px", marginLeft: "1em"}} id="profile-btn">Join Room</button></Link>   
            </div>

            <div id = "friends-room-container" className = "container" style = {friendsRoomContainer}>
                <div>
                    <div id = "server-names-container">
                       
                        {rooms.map((room)=>{
                            
                            return <div><a style = {{textDecoration: 'none'}} href={`/room/${room.roomname}`} ><h3>{room.roomname}</h3></a><hr style = {{borderTop: '1px solid black'}}/></div>
                            
                        })}
                           
                        
                    </div>
                    <div id = "people-name-container">

                    </div>
                        
                </div>
            </div>
        </>
    );

}

export default Home;
