import React from "react";
import { withRouter } from "react-router-dom";
import {io} from "socket.io-client";
import { useEffect, useState } from "react";
let socket = io.connect("http://localhost:5000");

function Room(props){

    let [roomMembers, setRoomMembers] = useState([]);
  
    useEffect(()=>{
       
        let getRoomData = async()=>{

             
            let response = await fetch('/api/room/'+props.match.params.roomname);
            let json = await response.json();

            if(response.ok){
               // console.log(json.members);
                
                   //for(let i = 0; i < json.members; i++){

                       
                     setRoomMembers(json.members);

                   //}

                   
                
                
            }
        }

        getRoomData();

       

      
    }, [])


    let membersChatContainer = {
        display: 'flex',
        flexDirection: 'column',
        marginLeft: '20%',
        marginRight: '20%',
        marginTop: '5%'

    }

    let memebersStyle = {
        background: 'white'
    }

    let chatAreaStyle = {
        background: 'white'
    }
    let chatcontainer = {
        height: '60vh'
    }

    let inputText = {
        width: '50%',
        height: '2em',
        marginBottom: '1em',
        border: '1px solid #294780',
        borderRadius: '0.3em'
    }

    

    

    socket.on('connect', ()=>{
        console.log("Connected to the client")
    })
        
        
    console.log(roomMembers);   

   

    return(
        <>
            <div style = {membersChatContainer} className="container" id = "memebers-chat-container">
                  
                  <div style = {memebersStyle} id = "members-drop-down-container">
                     {roomMembers.map((users)=>{
                        return <h2>{users}</h2>
                      })}
                  </div>
                    <br/>
                  <div style = {chatAreaStyle} id = "chat-area">
                       <div style = {chatcontainer} id = "chat-container">

                       </div>
                       <div id = "chat-inputs">
                            <span><input style = {inputText} type = "text" id = "msg-writer" placeholder="Write text..."/><button>Send</button></span>
                       </div>
                  </div>
                    
            </div>
        </>
    );

}

export default withRouter(Room);
